
public class Star {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		int j;
		int row=10;
		for(i=1;i<=row;i++) {
			for(j=row;j>=i;j--) {
				System.out.print("*");
			}
			System.out.println();
		}

	}

}
